          {/* ... rest of the code for rendering books in the cart */}
        </div>
        <div className='d-flex align-items-center justify-content-center p-5' style={{ width: '550px', height: 'auto' }}>
          <Card style={{ width: '18rem', borderRadius: '0px' }} className='d-flex align-items-center justify-content-center shadow'>
            {/* ... rest of the code for rendering summary section */}
          </Card>

          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Place Order</Modal.Title>
