import React, { useState, useEffect } from 'react';
import Header from '../Components/Header';
import { OrderAPI } from '../Services/allAPI';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate, useLocation } from 'react-router-dom';

function OrderForm() {
  const navigate = useNavigate();
  const location = useLocation();

  const [userdetails, setUserdetails] = useState({
    Bookname: '',
    Author: '',
    Price: '', // Assuming Price is a property of your book object
    name: '',
    mobileNumber: '',
    pincode: '',
    address: ''
  });

  useEffect(() => {
    // Check if location state has selectedBook and update UI accordingly
    if (location.state && location.state.selectedBook) {
      const selectedBook = location.state.selectedBook;
      // Update UI with selectedBook details as needed
      console.log('Selected Book:', selectedBook);
      // Example: Set the book details in the userdetails state
      setUserdetails({
        Bookname: selectedBook.bookname,
        Author: selectedBook.author,
        Price: selectedBook.price, // Assuming Price is a property of your book object
        name: '',
        mobileNumber: '', // Set other details as needed
        pincode: '',
        address: ''
      });
    }
  }, [location.state]);

  const handleclose = () => {
    setUserdetails({
      Bookname: '',
      Author: '',
      Price: '', // Assuming Price is a property of your book object
      name: '',
      mobileNumber: '',
      pincode: '',
      address: ''
    });
    navigate('/books');
  };

  const handleadd = async (e) => {
    e.preventDefault();
    const { name, mobileNumber, pincode, address } = userdetails;
    if (!name || !mobileNumber || !pincode || !address) {
      toast.info('Please fill the form completely');
    } else {
      const reqBody = new FormData();
      reqBody.append('name', name);
      reqBody.append('mobileNumber', mobileNumber);
      reqBody.append('pincode', pincode);
      reqBody.append('address', address);

      const reqHeader = {
        'Content-Type': 'application/json'
      };

      try {
        const res = await OrderAPI(reqBody, reqHeader);
        console.log(res);
        if (res.status === 200) {
          toast.success('Your Order Placed Successfully');
          // reset state
          setUserdetails({
            Bookname: '',
            Author: '',
            Price: '',
            name: '',
            mobileNumber: '',
            pincode: '',
            address: ''
          });
        } else {
          alert(res.response.data);
        }
      } catch (error) {
        console.error('Error placing order:', error);
        toast.error('Error placing order');
      }
    }
  };

  return (
    <>
      <Header />

      <div style={{ width: '100%', height: '100vh' }} className='d-flex align-items-center justify-content-center '>
        <div style={{ width: '600px', height: 'auto' }} className='shadow p-5'>
          <form onSubmit={handleadd}>
            <center>
              <h3 id='h3' className='text-dark mb-3'>
                BOOKIES
              </h3>
            </center>
            <div className='mb-3'>
              <label className='form-label'>Book Name</label>
              <input
                type='text'
                className='form-control'
                value={userdetails.Bookname}
                readOnly
              />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Author</label>
              <input
                type='text'
                className='form-control'
                value={userdetails.Author}
                readOnly
              />
            </div>

            <div className='mb-3'>
              <label className='form-label'>Price</label>
              <input
                type='text'
                className='form-control'
                value={userdetails.Price}
                readOnly
              />
            </div>

            {/* Other input fields for name, mobileNumber, pincode, address */}
            {/* ... */}
            <div style={{ width: '100%' }} className='d-flex align-items-center justify-content-between'>
              <button style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleclose} className='btn text-light'>
                Cancel
              </button>
              <button style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleadd} className='btn text-light ms-5'>
                Submit
              </button>
            </div>
          </form>
        </div>
        <ToastContainer theme='colored' autoClose={2000} position='top-right' />
      </div>
    </>
  );
}

export default OrderForm;
