import { useEffect, useRef, useState } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "./Header.css";
import { Link } from "react-router-dom";


function Navbar() {
	const navRef = useRef();
	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};
	 

    const [logedIn,setLogedIn] = useState(false)
	useEffect(()=>{
		if(sessionStorage.getItem("token")){
			setLogedIn(true)
			window.location.reload();
		}else{
			setLogedIn(false)
		}
	},[])
	useEffect(() => {
		// Reload the page when the login status changes
		
	  }, [logedIn]);

	return (
		<header >
			<h3 className="text-light">BOOKIES</h3>
			<nav ref={navRef}>
             <Link  to={'/'}>Home</Link>
			 { logedIn?
				<div>
					<Link to={'newarrival'} href="#">New Arrivals</Link>
	                <Link to={'bestseller'} href="#">Best Sellers</Link>
					<Link to={'contact'}>Contact Us</Link>
					<Link to={'/'}>Log Out</Link></div>:
					<Link to={'login'}>Log in</Link>
					}
				
				

                

				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
	);
}

export default Navbar;
