import React from 'react'
import {  useRef,use } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import { Link } from 'react-router-dom';

function Adminnav() {
    const navRef = useRef();
	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};
    const navigate = useNavigate()

    const handleLogout = () => {
		// Clear the token from sessionStorage
		sessionStorage.removeItem('token');
		// Update the loggedIn state
		setLogedIn(false);
		navigate('/')
		
		
	};
  return (
    <>
    <header >
			<div className='d-flex align-items-center justify-content-center justify-content-between p-3'>
			    <h3  className="text-light">BOOKIES</h3>
    			<nav className='mt-3 ms-5' ref={navRef}>
    			 
    				<div>
    					 <Link  to={'/adminhome'}>DashBoard</Link>
    					
    	                <Link to={'/Usermanage'}>Manage User</Link>
    					<Link to={'/booksmanage'}>Manage Books</Link>
    					<button id="btn" onClick={handleLogout} >Log Out</button>
    					</div>
    
    				<button
    					className="nav-btn nav-close-btn"
    					onClick={showNavbar}>
    					<FaTimes />
    				</button>
    			</nav>
    			<button
    				className="nav-btn"
    				onClick={showNavbar}>
    				<FaBars />
    			</button>
			</div>
		</header>
    </>
  )
}

export default Adminnav
