// ContextShare.js
import React, { createContext, useContext, useState } from 'react';

const addcontext = createContext();

const AddProvider = ({ children }) => {
  const [addthings, setAddthings] = useState([]);

  const addBookToCart = (book) => {
    setAddthings((prevBooks) => [...prevBooks, book]);
  };

  const removeBookFromCart = (bookId) => {
    setAddthings((prevBooks) => prevBooks.filter((book) => book._id !== bookId));
  };

  return (
    <addcontext.Provider value={{ addthings, addBookToCart, removeBookFromCart }}>
      {children}
    </addcontext.Provider>
  );
};

const useAddContext = () => {
  const context = useContext(addcontext);
  if (!context) {
    throw new Error('useAddContext must be used within an AddProvider');
  }
  return context;
};

export { AddProvider, useAddContext };
