// Addcart.jsx
import React, { useContext } from 'react';
import Card from 'react-bootstrap/Card';
import { Button } from 'react-bootstrap';
import { BASE_URL } from '../Services/baseurl';
import { useAddContext } from '../Components/ContextShare';
import Header from '../Components/Header';

function Addcart() {
  const { addthings, removeBookFromCart } = useAddContext();

  const numberOfBooks = addthings.length;
  const totalPrice = addthings.reduce((acc, book) => acc + book.price, 0);

  const handleRemoveBook = (bookId) => {
    removeBookFromCart(bookId);
  };

  return (
    <>
      <Header />
      <div style={{ width: "100%", height: "auto" }} className='d-flex align-items-center justify-content-center'>
        <div style={{ height: "auto%", width: "1250px" }} className='p-5'>
          <div className="container py-5">
            <div className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center">
              {addthings.length > 0 ? (
                addthings.map((book) => (
                  <div key={book._id} className="col d-flex align-items-center justify-content-center">
                    <div className="card shadow-lg">
                      <img
                        src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : "Uploads\\image-1701440853878-its.png"}
                        style={{ height: '300px', width: '100%' }}
                        className="img-fluid card-img-top"
                        alt={book.name}
                      />
                      <div className="card-body d-flex flex-column">
                        <div>
                          <h6>Book Name: {book.bookname}</h6>
                        </div>
                        <div>
                          <h6>Author: {book.author}</h6>
                        </div>
                        <div>
                          <h6>Price: {book.price}</h6>
                        </div>
                        <div className="d-flex justify-content-between align-items-center mt-auto">
                          <i
                            className="fa-solid fa-cart-shopping"
                            style={{ color: "#ff051e", cursor: "pointer" }}
                            onClick={() => handleRemoveBook(book._id)}
                          ></i>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <h2 className='text-danger text-center mt-5'>Cart Empty</h2>
              )}
            </div>
          </div>
        </div>
        <div className='d-flex align-items-center justify-content-center p-5' style={{ width: "550px", height: "auto" }}>
          <Card style={{ width: '18rem', borderRadius: "10px" }} className='d-flex align-items-center justify-content-center'>
            <Card.Img variant="top" src="https://static.vecteezy.com/system/resources/previews/002/896/415/original/books-illustration-cartoon-books-books-vector.jpg" alt="Books Illustration" />
            <Card.Body className='d-flex flex-column align-items-center justify-content-center'>
              <Card.Title>No of Books: {numberOfBooks}</Card.Title>
              <Card.Text>
                Total Price: ${totalPrice.toFixed(2)}
              </Card.Text>
              <Button variant="primary">Buy</Button>
            </Card.Body>
          </Card>
        </div>
      </div>
    </>
  );
}

export default Addcart;
