import React, { useEffect, useState } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './Books.css';
import { BASE_URL } from '../Services/baseurl';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Header from '../Components/Header'
import { Link } from 'react-router-dom';



function Books() {
  const [allbooks, setAllbooks] = useState([]);
  const [addedToCart, setAddedToCart] = useState(null);
 
  const getallbooks = async () => {
    const result = await allBooksAPI();

    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }


  useEffect(() => {
    getallbooks();
  }, []);

  
  const addToCart = (book) => {
    const itemToAdd = {
      bookname: book.bookname,
      author: book.author,
      price: book.price,
      image: book.bookImage, // Assuming this is the field containing the image URL
    };

    setCart([...cart, itemToAdd]);
    setAddedToCart(itemToAdd);
    
    toast.success(`Added ${book.bookname} to the cart!`);
  };

  return (
    <>
    <Header/>
  
      <div className="container py-5">
            <h1 id="h1" className="text-center">
              BOOKS
            </h1>
          <Link to={'/addcart'}> <button className='p-1 text-light' style={{width:"100px",borderRadius:"15px",backgroundColor:"#023246"}}> <i class="fa-solid fa-cart-plus me-2"></i>Cart</button></Link>
        <div
          id="card-cont"
          className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center"
        >
          {allbooks.map((book) => (
            <div key={book._id}   className="col d-flex align-items-center justify-content-center">
              <div className="card shadow-lg">
                <img src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : "Uploads\\image-1701440853878-its.png"} style={{ height: '300px', width: '100%' }} className="img-fluid card-img-top" alt={book.name} />
                <div className="card-body d-flex flex-column">
                  <div>
                    <h6>Book Name: {book.bookname}</h6>
                  </div>
                  <div>
                    <h6>Author: {book.author}</h6>
                  </div>
                  <div>
                    <h6>Price: {book.price}</h6>
                  </div>
                </div>
                <div>
                   <button id='button' className='btn btn-primary' onClick={() => addToCart(book)}>Add to cart</button>
                </div>
               
              </div>
            </div>
          ))}
        </div>
        <ToastContainer
          theme="colored"
          autoClose={2000}
          position="top-right" />
      </div>
    </>
  );
}

export default Books;
