export const commonAPI = async (method, url, data, headers) => {
    try {
      const response = await fetch(url, {
        method,
        headers,
        body: JSON.stringify(data),
      });
  
      const result = {
        status: response.status,
        data: await response.json(),
      };
  
      return result;
    } catch (error) {
      console.error('Error in commonAPI:', error);
      throw error;
    }
  };
