import React, { useEffect, useState } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './Books.css';
import { BASE_URL } from '../Services/baseurl';
import { deletebookAPI } from '../Services/allAPI';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function BooksManage() {
  const [allbooks, setAllbooks] = useState([]);
  const[deleteresponse,setresponse]=useState(1)

  const getallbooks = async () => {
    const result = await allBooksAPI();

    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }

  const handlebookdelete =async(e)=>{
    const reqBody=e._id
    const result=await deletebookAPI(reqBody)
    if(result.status==200){
      setresponse(deleteresponse+1)
    }
    else{
      alert("Action Failed !!! Please Try After Some Time...")
    }
  }

  useEffect(() => {
    getallbooks();
  }, [deleteresponse]);
  
  return (
    <>
    
    </>
  )
}

export default BooksManage
