import React, { useEffect, useState, useContext } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './Books.css';
import { BASE_URL } from '../Services/baseurl';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Header from '../Components/Header'
import { Link } from 'react-router-dom';
import { addcontext } from '../Components/ContextShare';
import Modal from 'react-bootstrap/Modal';
import { Button } from 'react-bootstrap';



function Books() {


  
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);




  
  const [allbooks, setAllbooks] = useState([]);
  const { addthings, setaddthings } = useContext(addcontext);

 
  const getallbooks = async () => {
    const result = await allBooksAPI();

    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }


  useEffect(() => {
    getallbooks();
  }, []);
  
  const handleCart = (e, book) => {
    e.preventDefault();
    setaddthings([...addthings, book]);
  };

  
  return (
    <>
    <Header/>
  
      <div className="container py-5">
            <h1 id="h1" className="text-center">
              BOOKS
            </h1>
          <Link to={'/addcart'}> <button className='p-1 text-light' style={{width:"100px",borderRadius:"15px",backgroundColor:"#023246"}}> <i class="fa-solid fa-cart-plus me-2"></i>Cart {addthings.length}</button></Link>
        <div
          id="card-cont"
          className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center"
        >
          {allbooks.map((book) => (
            <div key={book._id}   className="col d-flex align-items-center justify-content-center">
              <div className="card shadow-lg">
                <img src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : "Uploads\\image-1701440853878-its.png"} style={{ height: '300px', width: '100%' }} className="img-fluid card-img-top" alt={book.name} />
                <div className="card-body d-flex flex-column">
                  {/* <table style={{width:"100%",height:"100px"}}>
                    <tr>
                      <td>Book Name: </td>
                      <td>{book.bookname}</td>
                    </tr>
                    <tr>
                      <td>Author :</td>
                      <td>{book.author}</td>
                    </tr>
                    <tr>
                      <td>Price :</td>
                      <td>{book.price}</td>
                    </tr>
                  </table> */}
                  <div>
                    <h6>Book Name: {book.bookname}</h6>
                  </div>
                  <div>
                    <h6>Author: {book.author}</h6>
                  </div>
                  <div>
                    <h6>Price: {book.price}</h6>
                  </div>
                </div>
                <div className='p-2  d-flex align-items-center justify-content-between' style={{width:"100%",height:"auto"}}>
                <i
                            className="fa-solid fa-cart-shopping m-3"
                            style={{ cursor: "pointer" }}
                            onClick={(e) => handleCart(e, book)}
                          ></i>
                         <Button variant="" className='m-3 text-light' style={{width:"100px",backgroundColor:"#023246"}} onClick={handleShow}>
        Buy
      </Button>
                </div>
                
    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Order</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className=' d-flex align-items-center '>
            <h4>Bookname :</h4>
            <input className='ms-2 p-1' style={{width:"300px"}} type="text" />
          </div>
         
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="" style={{width:"150px",backgroundColor }} onClick={handleClose}>
            Place order
          </Button>
        </Modal.Footer>
      </Modal>
               
              </div>
            </div>
          ))}
        </div>
        <ToastContainer
          theme="colored"
          autoClose={2000}
          position="top-right" />
      </div>
    </>
  );
}

export default Books;
