import React, { useEffect, useState, useContext } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './Books.css';
import { BASE_URL } from '../Services/baseurl';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Header from '../Components/Header';
import { Link } from 'react-router-dom';
import { addcontext } from '../Components/ContextShare';
import Modal from 'react-bootstrap/Modal';
import { Button } from 'react-bootstrap';

function Books() {
  const [show, setShow] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null); 
  const handleClose = () => setShow(false);

  const handleShow = (book) => {
    setSelectedBook(book);
    setShow(true);
  };

  const [allbooks, setAllbooks] = useState([]);
  const { addthings, setaddthings } = useContext(addcontext);

  const getallbooks = async () => {
    try {
      const result = await allBooksAPI();
      if (result.status === 200) {
        setAllbooks(result.data);
      }
    } catch (error) {
      console.error('Error fetching books:', error);
      // Handle error, show a toast, etc.
      toast.error('Error fetching books');
    }
  };

  useEffect(() => {
    getallbooks();
  }, []);

  const handleCart = (e, book) => {
    e.preventDefault();
    setaddthings([...addthings, book]);
    toast.success('Book added to cart');
  };

  return (
    <>
      <Header />

      	  
<div className="container">

<h1 className="heading text-">BOOKS</h1>

<div className="box-container">
{allbooks.map((book) => (

    <div className="box">
        <img
                  src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : 'Uploads\\image-1701440853878-its.png'}
                  style={{ height: '300px', width: '100%' }}
                  className="img-fluid card-img-top"
                  alt={book.name}
                />
        <h6>Book Name: {book.bookname}</h6>
        <h6>Author: {book.author}</h6>
        <h6>Price: {book.price}</h6>
        <i
                    className="fa-solid fa-cart-shopping m-2 ms-2"
                    style={{ cursor: 'pointer' }}
                    onClick={(e) => handleCart(e, book)}
                  ></i>
                  <Button
                    variant=""
                    className='m-2 me-3 text-light'
                    style={{ width: '100px', backgroundColor: '#023246' }}
                    onClick={() => handleShow(book)}
                  >
                    Buy
                  </Button>
    </div>
     ))}
    </div>
    </div>

{/* 
      <div className="container py-5">
        <h1 id="h1" className="text-center">
          BOOKS
        </h1>
        <Link to={'/addcart'}>
          <button className='p-1 text-dark' style={{ width: '100px', borderRadius: '15px', background: 'transparent',border:"1px solid",borderColor:"blac" }}>
            <i className="fa-solid fa-cart-plus me-2"></i>Cart {addthings.length}
          </button>
        </Link>

        <div
          id="card-cont"
          className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center"
        >
          {allbooks.map((book) => (
            <div key={book._id} className="col d-flex align-items-center justify-content-center">
              <div className="card shadow-lg ">
                <img
                  src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : 'Uploads\\image-1701440853878-its.png'}
                  style={{ height: '300px', width: '100%' }}
                  className="img-fluid card-img-top"
                  alt={book.name}
                />
                <div className="card-body d-flex flex-column">
                  <div>
                    <h6>Book Name: {book.bookname}</h6>
                  </div>
                  <div>
                    <h6>Author: {book.author}</h6>
                  </div>
                  <div>
                    <h6>Price: {book.price}</h6>
                  </div>
                </div>
                <div className='p-1 d-flex align-items-center justify-content-between' style={{ width: '100%', height: 'auto' }}>
                  <i
                    className="fa-solid fa-cart-shopping m-2 ms-2"
                    style={{ cursor: 'pointer' }}
                    onClick={(e) => handleCart(e, book)}
                  ></i>
                  <Button
                    variant=""
                    className='m-2 me-3 text-light'
                    style={{ width: '100px', backgroundColor: '#023246' }}
                    onClick={() => handleShow(book)}
                  >
                    Buy
                  </Button>
                </div>
              </div>
            </div>
          ))}
        </div> */}
{/* 
        <ToastContainer theme="colored" autoClose={2000} position="top-right" />
      </div> */}

      {/* Modal for buying a book */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Order</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div style={{ width: '100%', height: 'auto' }} className='d-flex align-items-center justify-content-between m-2 justify-content-center '>
            <img
              src={selectedBook ? `${BASE_URL}/Uploads/${selectedBook?.bookImage}` : 'Uploads\\image-1701440853878-its.png'}
              style={{ height: '350px', width: '300px',marginLeft:"70px" }}
              className='img-fluid card-img-top'
              alt={selectedBook?.name}
            />
          </div>
         <div style={{width:"100%",height:"auto"}} className='d-flex align-items-center justify-content-between'>
            <table style={{width:"100%"}}>
              <tr >
                <td className='text-center'><h5 className='text-dark'>Book Name :</h5></td>
                <td><span >{selectedBook?.bookname}</span></td>
              </tr>
              <tr>
                <td className='text-center'><h5 className='text-dark'>Author :</h5></td>
                <td><span >{selectedBook?.author}</span></td>
              </tr>
              <tr>
                <td className='text-center'><h5 className='text-dark'>Price :</h5></td>
                <td><span >{selectedBook?.price}</span></td>
              </tr>
              <tr>
              <p>Payment Method: Cash on Delivery</p>
              </tr>
            </table>
         </div>
          
        </Modal.Body>
        <Modal.Footer>
       
          <Button variant='' className='text-light' style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleClose}>
            Close
          </Button>
          
          <Link to={'/orderform'}>
            <Button variant='' className='text-light' style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleClose}>
              Place order
            </Button>
          </Link>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Books;
