import React, { useState } from 'react'
import Header from '../Components/Header'
import { OrderAPI } from '../Services/allAPI'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function OrderForm() {
  const [userdetails,setUserdetails] = useState({
    name:"",mobilenumber:"",pincode:"",address:""
  })

  const handleclose = () =>{
    setUserdetails({
      name:"",mobilenumber:"",pincode:"",address:""
    })
  }

  
  const handleadd = async (e) => {
    e.preventDefault()
    const {name,mobilenumber,pincode,address} = userdetails
   
  };

  

  return (
    <>
    <Header/>
    
    <div style={{width:"100%",height:"100vh"}} className='d-flex  align-items-center justify-content-center '>
      
   <div style={{width:"600px",height:"600px"}} className='shadow p-5'>
      <form onSubmit={handleadd}>
     <center> <h3 id="h3" className="text-dark mb-3">BOOKIES</h3></center>
    <div className="mb-3">
      <label  className="form-label">Name</label>
      <input type="text" className="form-control"  aria-describedby="emailHelp"  value={userdetails.name} onChange={e=>setUserdetails({...userdetails,name:e.target.value})}/>
     
    </div>
    <div className="mb-3">
      <label  className="form-label">Mobile Number</label>
      <input type="text" className="form-control"  value={userdetails.mobilenumber} onChange={e=>setUserdetails({...userdetails,mobilenumber:e.target.value})}/>
    </div>
    <div className="mb-3 ">
    <label  className="form-label">Pincode</label>
      <input type="text" className="form-control"  value={userdetails.pincode} onChange={e=>setUserdetails({...userdetails,pincode:e.target.value})}/>
    </div>
    <div className="mb-3 ">
    <label  className="form-label">Address</label><br />
    <textarea
  id='comment'
  style={{ width: "100%" }}
  maxLength={100}
  className='bg-gray-200 p-1 h-20 w-full mt-0 good'
  value={userdetails.address}
  onChange={(e) => setUserdetails({ ...userdetails, address: e.target.value })}
/>

    </div>
   <div style={{width:"100%"}} className='d-flex align-items-center justify-content-between'>
      <button  style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleclose}  className="btn text-light">Cancel</button>
      <button style={{ width: '150px', backgroundColor: '#023246' }} onClick={handleadd} className="btn text-light ms-5"> Submit</button>

   </div>
  </form>
   </div>
   <ToastContainer 
theme="colored"
autoClose={2000}
position="top-right"/>
    </div>
    </>
  )
}

export default OrderForm
