import React, { useContext } from 'react';
import { BASE_URL } from '../Services/baseurl';
import { addcontext } from '../Components/ContextShare';
import Header from '../Components/Header'

function Addcart() {
  const { addthings } = useContext(addcontext);

  return (
   <>
   <Header/>
<div style={{width:"100%",height:"auto"}} className='d-flex'>
  <div style={{height:"auto%",width:"1250px"}} className='p-5'>
          <div className="container py-5">
            <div className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center">
              {addthings.length > 0 ? (
                addthings.map((book) => (
                  <div key={book._id} className="col d-flex align-items-center justify-content-center">
                    <div className="card shadow-lg">
                      <img
                        src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : "Uploads\\image-1701440853878-its.png"}
                        style={{ height: '300px', width: '100%' }}
                        className="img-fluid card-img-top"
                        alt={book.name}
                      />
                      <div className="card-body d-flex flex-column">
                        <div>
                          <h6>Book Name: {book.bookname}</h6>
                        </div>
                        <div>
                          <h6>Author: {book.author}</h6>
                        </div>
                        <div>
                          <h6>Price: {book.price}</h6>
                        </div>
                        <i class="fa-solid fa-cart-shopping" style={{color:"#ff051e"}}></i>
                      </div>
                      
                    </div>
                  </div>
                ))
              ) : (
                <h2 className='text-danger text-center mt-5'>Cart Empty</h2>
              )}
            </div>
          </div>
  </div>
  <div assName='bg-warning' style={{width:"550px"}}>
    <div className='d-flex align-items-center justify-content-center'>
      <div style={{width:"100px",height:"200px"}} className='bg-warning'></div>
    </div>
  </div>
</div>
   </>
  );
}

export default Addcart;
