    const result = await allBooksAPI();

    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }

  const handlebookdelete =async(e)=>{
    const reqBody=e._id
    const result=await deletebookAPI(reqBody)
    if(result.status==200){
      setresponse(deleteresponse+1)
    }
    else{
      alert("Action Failed !!! Please Try After Some Time...")
    }
  }

  useEffect(() => {
    getallbooks();
  }, [deleteresponse]);
  
