import React, { useEffect, useState } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './BooksManage.css';
import { BASE_URL } from '../Services/baseurl';
import { deletebookAPI } from '../Services/allAPI';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom';
import Adminnav from './Adminnav';

function BooksManage() {
  const [allbooks, setAllbooks] = useState([]);
  const[deleteresponse,setresponse]=useState(1)

  const getallbooks = async () => {
    const result = await allBooksAPI();

    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }

  const handlebookdelete =async(e)=>{
    const reqBody=e._id
    const result=await deletebookAPI(reqBody)
    if(result.status==200){
      setresponse(deleteresponse+1)
    }
    else{
      alert("Action Failed !!! Please Try After Some Time...")
    }
  }

  useEffect(() => {
    getallbooks();
  }, [deleteresponse]);
  
  return (
    <>
    <Adminnav/>
     <div className="container py-5">
        <h1 id="h1" className="text-center">
          BOOKS
        </h1>
        <div
          id="card-cont"
          className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex  flex-wrap align-items-center justify-content-center"
        >
         
          {allbooks.map((book) => (
            <div key={book._id}   className="col d-flex align-items-center justify-content-center">
              <div className="card shadow-lg">
                <img src={book ? `${BASE_URL}/Uploads/${book?.bookImage}` : "Uploads\\image-1701440853878-its.png"} style={{ height: '300px', width: '100%' }} className="img-fluid card-img-top" alt={book.name} />
                <div className="card-body d-flex flex-column">
                  <div>
                    <h6>Book Name: {book.bookname}</h6>
                  </div>
                  <div>
                    <h6>Author: {book.author}</h6>
                  </div>
                </div>
                <div className="mb-3 d-flex justify-content-center">
                  <button className="btn btn-primary" onClick={e=>handlebookdelete(book)}>Delete</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <ToastContainer
          theme="colored"
          autoClose={2000}
          position="top-right" />
      </div>
    
    </>
  )
}

export default BooksManage
