import React, { useEffect, useState } from 'react';
import { allBooksAPI } from '../Services/allAPI';
import './Books.css';
import { BASE_URL } from '../Services/baseurl';

function Books() {
  const [allbooks, setAllbooks] = useState([]);

  const getallbooks = async () => {
    const result = await allBooksAPI();
    
    if (result.status === 200) {
      setAllbooks(result.data);
    }
  }
  console.log(allbooks);

  useEffect(() => {
    getallbooks();
  }, []);

  return (
    <>
      <div className="container py-5">
        <h1 id="h1" className="text-center">
          BOOKS
        </h1>
        <div
          id="card-cont"
          className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center"
        >
          {allbooks.map((book) => (
            <div key={book._id} className="col d-flex align-items-center justify-content-center">
              <div className="card shadow-lg">
              <img src={Books? `${BASE_URL}/Uploads/${Books?.bookImage}` : "Uploads\\image-1701440853878-its.png"} style={{ height: '300px', width: '100%' }} className="img-fluid card-img-top" alt={book.name}/>
                <div className="card-body d-flex flex-column">
                  <div>
                    <h6>Book Name: {book.bookname}</h6>
                  </div>
                  <div>
                    <h6>Author: {book.author}</h6>
                  </div>
                  <div>
                    <h6>Price: ${book.price}</h6>
                  </div>
                </div>
                <div className="mb-3 d-flex justify-content-center">
                  <button className="btn btn-primary">Buy Now</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Books;
