import React from 'react'
import Adminnav from './Adminnav'

import { useState,useEffect } from 'react'
import { allordersAPI,deleteorder } from '../Services/allAPI'

function Orders() {
  const[allorders,setAllorders]=useState([])
  const [delresponse,setdelresponse]=useState(1)

  


  const getorderdetails = async()=>{
    const result = await allordersAPI()
    //  console.log(result.data);
    setAllorders(result.data)
    
 }


 
 const handledeleteorder = async(e)=>{
  // console.log(e);
  console.log(e._id);
  const reqBody = e._id
  const res = await deleteorder(reqBody)
  if(res.status==200){
    setdelresponse(delresponse+1)
  }
  else{
    alert("Action Failed!!! Please try after some time")
  }
  
}

 useEffect(()=>{
  getorderdetails()
},[delresponse])
  return (
    <>
    <Adminnav/>
    <div className='d-flex flex-column align-items-center justify-content-center justify-content-between p-3' style={{width:"100%",height:"auto"}}>
    <h1>Orders Table</h1>
       
       <table className='rounded shadow-lg bg-dark text-light border ' style={{width:"900px"}}>
          <thead className='border'>
        
              <tr >
                  <th className='p-2 text-center '>Book name</th>
                  <th className='p-2 text-center'>Price</th>
                  <th className='p-2 text-center'>Name</th>
                  
              </tr>
          </thead>
          
          <tbody>
          {
      allorders.length>0?allorders.map((order)=>(
        <tr className='text-center'>
       <td className='p-3'>{order.bookname}</td>
       <td>{order.Price}</td>
       <td>{order.name}</td>
       <td><button className='btn btn-danger' onClick={handledeleteorder} >Block User</button></td>
     </tr>
      )): <h3 className='text-danger text-center'>No Orders</h3>
     }
          </tbody>
      </table> 

    </div>
    </>
  )
}

export default Orders
