import React from 'react'
import Adminnav from './Adminnav'

function Orders() {
  const[allorders,setAllorders]=useState([])

  


  const getorderdetails = async()=>{
    const result = await allordersAPI()
    //  console.log(result.data);
    set(result.data)
    
 }

 useEffect(()=>{
  getorderdetails()
},[])
  return (
    <>
    <Adminnav/>
    <div className='d-flex align-items-center justify-content-center justify-content-between p-3' style={{width:"100%",height:"100vh"}}>
    <h1>Users Table</h1>
       
       <table className='rounded shadow-lg bg-dark text-light border ' style={{width:"900px"}}>
          <thead className='border'>
        
              <tr >
                  <th className='p-2 text-center '>Username</th>
                  <th className='p-2 text-center'>Password</th>
                  <th className='p-2 text-center'>Email</th>
                  <th className='p-2 text-center'>Actions</th>
              </tr>
          </thead>
          
          <tbody>
          {
      allusers.length>0?allusers.map((users)=>(
        <tr className='text-center'>
       <td className='p-3'>{users.username}</td>
       <td>{users.password}</td>
       <td>{users.email}</td>
       <td><button className='btn btn-danger' onClick={e=>handledeleteuser(users)}>Block User</button></td>
     </tr>
      )): <h3 className='text-danger text-center'>No Users</h3>
     }
          </tbody>
      </table> 

    </div>
    </>
  )
}

export default Orders
