import React from 'react'

function Addcart() {
  return (
    <>
     {addthings.length > 0 ? (
        <div className='mt-5 ms-5'>
          <Row>
            {addthings.map((book) => (
              <Col sm={12} md={6} lg={4} xl={3} key={book.id}>
                <Card style={{ width: '18rem', height: 'auto', marginBottom: '20px' }} key={book.id}>
              <Card.Img variant="top" style={{ width: '100%', height: '250px' }} src={book.image} />
              <Card.Body>
                <Card.Title class="overflow-hidden" style={{ width: '100%', height: '100px' }}>{book.boo}</Card.Title>
                <Card.Text><h4>Price : {products.price}</h4></Card.Text>
                
              </Card.Body>
            </Card>
              </Col>
            ))}
          </Row>
        </div>
      ) : (
        <h2 className='text-danger text-center mt-5'>Cart Empty</h2>
      )}
    </>
  )
}

export default Addcart
