import React from 'react';
import { useRef } from 'react';
import { FaBars, FaTimes } from 'react-icons/fa';
import { Link, useHistory } from 'react-router-dom'; // Import useHistory

function Adminnav() {
  const navRef = useRef();
  const history = useHistory(); // Use useHistory instead of useNavigate

  const showNavbar = () => {
    navRef.current.classList.toggle('responsive_nav');
  };

  const handleLogout = () => {
    // Clear the token from sessionStorage
    sessionStorage.removeItem('token');
    
    // Navigate to the home page using history.push
    history.push('/');
  };

  return (
    <header>
      <div className="d-flex align-items-center justify-content-center justify-content-between p-3">
        <h3 className="text-light">BOOKIES</h3>
        <nav className="mt-3 ms-5" ref={navRef}>
          <div>
            <button id="btn" onClick={handleLogout}>
              Log Out
            </button>
          </div>
          <button className="nav-btn nav-close-btn" onClick={showNavbar}>
            <FaTimes />
          </button>
        </nav>
        <button className="nav-btn" onClick={showNavbar}>
          <FaBars />
        </button>
      </div>
    </header>
  );
}

export default Adminnav;
