import React, { useEffect, useState } from 'react'
import { allBooksAPI } from '../Services/allAPI'
import './Books.css'
function Books() {

  const [allbooks,setAllbooks] = useState([])

  const getallbooks = async()=>{
      const result = await allBooksAPI()
      if(result.status===200){
        setAllbooks(result.data)
      }
     
    }


  useEffect(()=>{
    getallbooks()

  },[])
  return (
    <>
    <div className="container py-5 ">
        <h1 id='h1' className="text-center">BOOKS</h1>
        <div id='card-cont'  className="row row-cols-1 row-cols-md-4 g-4 py-1 d-flex flex-wrap align-items-center justify-content-center ">
        <div className="col d-flex align-items-center justify-content-center">


  
                  <div className="card shadow-lg">
                      <img src="" className="card-img-top " alt="..."/>
                      <div className="card-body d-flex flex-column">
                         <div> <h5>Book Name :</h5></div>
                          <div><h5>Authur:</h5></div>
                         <div> <h5>Price :</h5></div>
  
                      </div>
                      <div className="mb-3 d-flex justify-content-center">
                         
                          <button className="btn btn-primary">Buy Now</button>
                      </div>
  


                </div>
            </div>
        </div>
    </div>
    </>
  )
}

export default Books
